import React from "react";
import {
  Row,
  Col,
  TimePicker,
  Button,
  Radio,
  Card,
  Checkbox,
  Select
} from "antd";
import moment from "moment";
import { Link, RouteComponentProps } from "@reach/router";

type StepTempo = {
  Condizione: string;
  Checkbox: string;
  Festivo: string;
  TuttiGG: string;
  Time1: number;
  Time2: number;
  Time3: number;
  Time4: number;
  Stagioni: string;
  Temp: string;
  TempAttuale: string;
  TempMax: string;
  Meteo: string;
};

interface Props extends RouteComponentProps {
  dataForm: StepTempo;
  //onNextPage(event: React.ChangeEvent<HTMLInputElement>): void;
  onNextPage: Function;
  setDataForm: Function;
}

export const StepTempo: React.FC<Props> = ({
  dataForm,
  setDataForm,
  onNextPage
}) => {
  const { Option } = Select;
  const option = [
    { label: "Condizione temporale", value: "Condizione temporale" },
    { label: "Sempre", value: "Sempre" }
  ];

  const week = [
    { label: "Lun", value: "Lun" },
    { label: "Mar", value: "Mar" },
    { label: "Mer", value: "Mer" },
    { label: "Gio", value: "Gio" },
    { label: "Ven", value: "Ven" },
    { label: "Sab", value: "Sab" },
    { label: "Dom", value: "dom" }
  ];

  const format = "HH:mm";

  const onChange = () => {
    localStorage.setItem("Step Tempo", JSON.stringify(dataForm));
    onNextPage();
    console.log(JSON.stringify(dataForm));
  };

  return (
    <div>
      <div>
        <Row>
          <Col span={12}>
            <Radio.Group
              name="Condizione"
              options={option}
              value={dataForm.Condizione}
              onChange={event => {
                console.log(event);
                setDataForm({
                  ...dataForm,
                  StepTempo: {
                    ...dataForm,
                    Condizione: event.target.value
                  }
                });
              }}
            ></Radio.Group>
          </Col>
        </Row>
      </div>
      <br></br>
      <div style={{ paddingLeft: 100 }}>
        {dataForm.Condizione === "Condizione temporale" && (
          <Card title="Condizione Temporale" style={{ width: 800 }}>
            <Checkbox.Group
              value={[dataForm.Checkbox]}
              name="Checkbox"
              options={week}
              defaultValue={["Lun"]}
              onChange={value => {
                setDataForm({
                  ...dataForm,
                  StepTempo: {
                    ...dataForm,
                    Checkbox: value
                  }
                });
              }}
            />
            <Checkbox
              value={dataForm.Festivo}
              name="Festivo"
              defaultChecked={false}
              onChange={value => {
                setDataForm({
                  ...dataForm,
                  StepTempo: {
                    ...dataForm,
                    Checkbox: value
                  }
                });
              }}
            >
              Festivo
            </Checkbox>
            <div>
              <Checkbox
                value={dataForm.TuttiGG}
                name="TuttiGG"
                style={{ paddingLeft: 444 }}
                defaultChecked={false}
                onChange={value => {
                  setDataForm({
                    ...dataForm,
                    StepTempo: {
                      ...dataForm,
                      Checkbox: value
                    }
                  });
                }}
              >
                Tutti i GG
              </Checkbox>
            </div>
            <div style={{ paddingLeft: 100 }}>
              <Row gutter={16}>
                <Col span={6}>
                  <p>Ora Inizio</p>
                  <TimePicker
                    value={moment(dataForm.Time1)}
                    format={format}
                    onChange={(time: moment.Moment) =>
                      setDataForm({
                        ...dataForm,
                        StepTempo: {
                          ...dataForm,
                          Time1: time.valueOf()
                        }
                      })
                    }
                  />
                </Col>
                <Col span={6}>
                  <p>Ora Fine</p>
                  <TimePicker
                    value={moment(dataForm.Time2)}
                    format={format}
                    onChange={(time: moment.Moment) =>
                      setDataForm({
                        ...dataForm,
                        StepTempo: {
                          ...dataForm,
                          Time2: time.valueOf()
                        }
                      })
                    }
                  />
                </Col>
                <p></p>
                <br></br>
                <Col span={6}>
                  <Button>Rimuovi</Button>
                </Col>
              </Row>
              <p></p>
